import React, { Component } from "react";
import PostsTable from "./postsTable";

class Posts extends Component {

constructor(props) {
	super(props);
	this.state = {
		error: null,
		isLoaded: false,
		items: []
	};
}

componentDidMount() {
	fetch("https://taw-posts.herokuapp.com/api/posts")
		.then(res => res.json())
		.then(
			(result) => {
				this.setState({
					isLoaded: true,
					items: result
				});
				console.log(result)
			},
			(error) => {
				this.setState({
					isLoaded: true,
					error
				});
			}
		)
}

render() {
	const { items } = this.state;
	if (!items.length) {
		return <p>Brak wpis√≥w</p>
	}
	return (<PostsTable items={items} />)
}
}

export default Posts;
